import axios from 'axios';

let API_URL = 'http://127.0.0.1:8000';
let MobileAPI_URL = 'http://192.168.43.101:3000'





export default class Axios {

    constructor(props){
        console.log(props)

    }


    axiosInstance(with_token=true, tokenKey=null){
       //console.log(with_token, tokenKey)  

       	let baseURL = API_URL;

    	if(window.navigator.platform === 'Linux armv7l'){
          baseURL =  MobileAPI_URL;
        }

        var instance = axios.create({
         baseURL: baseURL,
      });

      //alert(with_token) 

      if (with_token) {
         //retrive token from storage
         let auth = localStorage.getItem('auth' )?true : false;
         if (auth) {
            auth  = localStorage.getItem('auth');
            auth = JSON.parse(auth)
            tokenKey = auth.tokenKey;
         }
         if (tokenKey || auth) {
            tokenKey =`Token ${tokenKey}`;
            instance.defaults.headers.common['Authorization'] = tokenKey;
         }
      }

      instance.defaults.timeout = 30000;
      return instance;
   };

}
