import React, { Component } from 'react';

import  * as action  from 'actions/actionCreators';
import { getQuestion } from 'dispatch/index';
import {PartalNavigationBar,NavigationBarBigScreen } from "components/navBar";
import  AjaxLoader from "components/ajax-loader";
import { QuestionComponent} from "components/question_components"
import {store} from "index";
import withMain  from 'containers/higher-order-components/main';





class QuestionPage extends Component {

   constructor(props) {
      super(props);

         this.state = {
            isQuestionBox : true, 
            pageName      : "Question", 
            questionById  : ''
         };

      };

   componentWillUnmount() {
   }

   componentWillMount() {
    //console.log(this.props)

   }

   componentDidUpdate(nextProps, prevState) {
     // alert('hi')
   }    


   componentDidMount() {

      let { isNeQuestion, questionObj } = this.props.location.state;
      this.setState({questionById:`question${questionObj.id}`})
      if (isNeQuestion) {
         store.dispatch(action.getQuestionSuccess(questionObj))
         store.dispatch(action.Redirected());
      }
      else {
         store.dispatch(getQuestion(questionObj.id));
      }
   };



   componentDidCatch(error, info) {
    // You can also log the error to an error reporting service
    console.log(error, info);
  }

   getProps(){
      //Collect all state data and props.
      let props = {
         isQuestionBox     : this.state.isQuestionBox,
         pageName          : this.state.pageName,
         questionById      : this.state.questionById

      };
      return Object.assign(props, this.props );  

   };

   render() {
      let props = this.getProps();
      var questionById = props.questionById;
      var question = props.entyties.question;
      question = question.byId[questionById]


      return (

         <div>
            <PartalNavigationBar {...props}/>
            <NavigationBarBigScreen {...props} />
            { question?
               <div>
                  { question.isLoading?
                     <div className="page-spin-loder-box">
                        <AjaxLoader/>
                     </div>
                     :
                     <div>
                        { question.questionObj?
                           <Questions {...props}/>
                           :
                           ""
                        }
                     </div>
                  } 
               </div>
               :
               ""  
            }           
         </div>


    );
  };
}


export default  withMain(QuestionPage);











export const Questions = props => {
   var questionById = props.questionById;
   var question = props.entyties.question;
   question = question.byId[questionById]
   let questionProps = { questionObj: question.questionObj };
   Object.assign(questionProps, props) 


   return (
      <div>
         <div>
            <QuestionComponent {...questionProps}/>
         </div>
      </div>
   );

};
