import React,{Component} from 'react';
import withMain  from 'containers/higher-order-components/main';
import { UserList } from "components/profile_components";
import {PartalNavigationBar} from "components/navBar";
import  AjaxLoader from "components/ajax-loader";
import  * as action  from 'actions/actionCreators';
import {store} from "index";


import Api from 'api';

const api = new Api();




class UserListBox extends Component {

   constructor(props) {
      super(props);
      this.state = {
         usersById : `allUsersList`,
         pageName       : "",
      }
   };


    componentDidMount() {
        console.log(this.props)
        var usersById  = ``;
        let  pageName  = 'Upvoters';
        var apiUrl     =   ''; 


        let { state }      = this.props.location;
        let usersProps = {};

        usersProps['pageName']  = 'Upvoters'; 
        usersProps['apiUrl']    = api.getUserListApi();
        usersProps['usersById'] = this.state.usersById;


        console.log(state)

        switch(state){
            case state.userProfile:

                usersProps = this.prepareUserFollowersProps(usersProps);
                this.updateLocalstate(usersProps);
                return this.props.getUserList(usersProps);


            case state.question:

                usersProps = this.prepareQuestionFollowersProps(usersProps);
                this.updateLocalstate(usersProps);
                return this.props.getUserList(usersProps);


            case state.post:

                usersProps = this.preparePostUpVotersProps(usersProps);
                this.updateLocalstate(usersProps);

                return this.props.getUserList(usersProps); 


            case state.answer:

                usersProps = this.prepareAnswerUpVotersProps(usersProps);
                this.updateLocalstate(usersProps);
                return this.props.getUserList(usersProps);


            case state.comment:

                usersProps = this.prepareCommentUpVotersProps(state);
                this.updateLocalstate(usersProps);
                return this.props.getUserList(usersProps);


            case state.reply:

                usersProps = this.prepareUserFollowersProps(state);
                this.updateLocalstate(usersProps);
                return this.props.getUserList(usersProps);


            default:
                state['pageName'] = 'users';

                this.updateLocalstate(state);
                return this.props.getUserList(state);

        }

    };

    updateLocalstate(state){
       this.setState(state);
    }

    prepareUserFollowersProps(params ){
        var {
            userProfile, 
            isUserFollowers, 
            isUserFollowings,
            usersById,
            apiUrl, 
            pageName,

        } = params;

        if (isUserFollowers) {

            pageName  = 'Followers';
            usersById = `userProfileFollowers${userProfile.id}`;
            apiUrl     = api.getUserFollowersListApi(userProfile.id)
        }
        else {

            pageName  = 'Followings';
            usersById = `answerReplyUpVoters${userProfile.id}`;
            apiUrl    = api.getUserFollowingsListApi(userProfile.id);
        }

        return { usersById, apiUrl, pageName};
    };



    prepareQuestionFollowersProps( params ) {

       var  { question, usersById, apiUrl, pageName } = params

        var pageName = 'Followers';
        usersById = `questionFollowers${question.id}`;
        apiUrl  = api.getQuestionFollowersListApi(question.id);

        return { usersById, apiUrl, pageName };
    };


    preparePostUpVotersProps( params ){

        var { post, usersById, apiUrl } = params;

        usersById = `postUpVoters${post.id}`;
        apiUrl   = api.getPostUpVotersListApi(post.id);

        return { usersById, apiUrl };
    };

    prepareAnswerUpVotersProps(params){

        var {answer, usersById, apiUrl } = params;

        usersById = `postUpVoters${answer.id}`;
        apiUrl   = api.getAnswerUpVotersListApi(answer.id);

        return { usersById, apiUrl};

    };

    prepareCommentUpVotersProps(params){

        var {comment, isAnswerBox, usersById, apiUrl} = params;
        if (isAnswerBox) {

            usersById  = `answerCommentUpVoters${comment.id}`;
            apiUrl     = api.getAnswerCommentUpVotersListApi(comment.id)

        }
        else{

            usersById = `postCommentUpVoters${comment.id}`;
            apiUrl    = api.getPostCommentUpVotersListApi(comment.id)
        }

        return { usersById, apiUrl};
    };

    prepareReplyUpVotersProps(params){
        var {reply, isAnswerBox, usersById, apiUrl} = params;

        if (isAnswerBox) {

            usersById = `answerReplyUpVoters${reply.id}`;
            apiUrl = api.getAnswerReplyUpVotersListApi(reply.id);

        }
        else {

            usersById = `postReplyUpVoters${reply.id}`;
            apiUrl = api.getPostReplyUpVotersListApi(reply.id);
        }

        return { usersById, apiUrl};

    }






   getProps(){
      let props = {
         pageName       : this.state.pageName,
         usersById      : this.state.usersById,
      };
      return Object.assign(props, this.props);
   };

   render() {
      var props = this.getProps()
      console.log(props)
      var usersById = props.usersById;
      const users = props.entyties.users.byId[usersById];
      //userProfile = userProfile.user;
      console.log(users)

      return (
          <div>
            <PartalNavigationBar {...props}/>
            {users?
               <div>
                  {users.isLoading?
                     <div className="page-spin-loder-box">
                        <AjaxLoader/>
                     </div>
                    :
                    <div style={{ paddingTop : '70px'}}>
                     <UserList {...props}/>
                     </div>
                  }
               </div>
               :
               ""
            }
          </div>
      )
   };
};



export default withMain(UserListBox);
