import React, { Component } from 'react';
import TextareaAutosize from 'react-autosize-textarea';
import { ModalManager} from 'react-dynamic-modal';

import {EditProfileNavBar,PartalNavigationBar,NavigationBarBigScreen } from "components/navBar";
import { EditorNavBar} from "components/navBar";
import {  ModalCloseBtn  } from "components/buttons";
import withMain  from 'containers/higher-order-components/main';

import  AjaxLoader from "components/ajax-loader";
import Axios from 'axios_instance'
import  Helper from 'containers/utils/helpers';
import Api from 'api';



const api = new Api();

const axiosApi = new Axios(); 
const helper   = new Helper();  




class EditProfile extends Component{

   constructor(props) {
      super(props);

      this.state = {
         currentUser  :  '',
         userProfile  :  '',
         form         : {
               profile_picture  : "",
               first_name       : "",
               last_name        : "",
               credential       : "",
               live             : "",
               favorite_quote   : "",
               phone_number     : "",

         },

      };
      this.openModal      =  this.openModal.bind(this);
      this.handleChange   =  this.handleChange.bind(this)
      this.handleSubmit   =  this.handleSubmit.bind(this)
   }


   componentDidMount() {
      console.log(this.props)
      if ( this.props.location &&  this.props.location.state) {
         if (this.props.location.state.userProfile || this.props.location.state.currentUser) {
            let userProfile = this.props.location.state.userProfile;
            let currentUser = this.props.location.state.currentUser;
            let form        =  this.state.form;
            let profile     = userProfile.profile;
            let {profile_picture,live,credential,favorite_quote} = profile 
            let {first_name,last_name} = userProfile

            form    = {first_name,last_name,profile_picture,live,credential,favorite_quote}

            this.setState({userProfile, currentUser,form,});
         }
      }
   };


   openModal(params){

   }

   handleChange(e){
      e.preventDefault()
      let form = this.state.form;
      form[e.target.name] = e.target.value;
      this.setState({form})
  }

  getProfileData(params) {

      let props          =   this.getProps();
      props['obj']       =   params.userProfile;
      props['objId']     =   params.userProfile.id;
      props['objName']   =  'userProfile';
      props['isEdit']    =  true;
      props['isPut']     =  true;
      props['apiUrl']    =  api.updateProfileApi(this, params.userProfile.id);


      return props;
   }


   getProps(){
      return {
         currentUser   : this.state.currentUser,
         userProfile   : this.state.userProfile,
         openModal     : this.openModal,
         handleChange  : this.handleChange, 
         handleSubmit  : this.handleSubmit,
         form          : this.state.form,
         textAreaProps : this.textAreaProps(),
      }

   }

   textAreaProps() {
      return {
        value     : this.state.form.favorite_quote,
        onChange  : this.handleChange,
        name      : "favorite_quote",
        className : "favorite_quote"
      };
   };


   handleSubmit(params) {
      console.log(params)
      let props = this.getProfileData(params);
      let userProfile = this.state.userProfile;
      let  {first_name,last_name,live,credential,favorite_quote} = this.state.form;
      let form = {};
      if (params.isImageDrop) {
         form = {profile_picture: params.file}
         props['formData']  = helper.creatFormData(form);

      }else {
         let form = {first_name,last_name,live,credential,favorite_quote};
         console.log(form)
         props['formData']  = helper.creatFormData(form);

      }

      helper.sendRequest(this, props)
   }

   render() {
      let props = this.getProps();
      return (
         <div>
            <EditProfileNavBar {...props}/>
            <NavigationBarBigScreen {...props} />

            { props.userProfile === ""? 
              <AjaxLoader/>
            : 
              <ProfileEditComponent {...props}/>   
            }

         </div>
      );
   };
};


export default EditProfile;

const ProfileEditComponent = props => {
   //console.log(props) 
   return(
      <div  className="edit-profile-container" >

         <div className="edit-img-container">
            <div className="item-title-box">
               <b className="item-title">Photo</b>
            </div>

            <div className="item-title-container">
               <div className="image-contain">
                  {props.userProfile.profile.profile_picture?
                     <div className="edit-image-box">
                        <img alt="" className="edit-image" src={props.userProfile.profile.profile_picture}/>
                     </div>
                     :
                     <div className="avatar-image-box">
                        <img alt="" src={require("images/user-avatar.png")} className="avatar-image"/> 
                     </div> 
                  }

               <EditBtn {...props}/>
               </div>

            </div>

         </div>   


         <div className="edit-name-box">
            <div className="item-title-container">
               <div className="item-title-box">
                 <b className="item-title"> Name</b>
               </div>
            </div>

            <div className="name-box">
               <input
                  type="text" 
                  className="user-profile-name"
                  name="first_name"
                  value={props.form.first_name}
                  onChange={props.handleChange}
               />

               <input
                  type="text" 
                  className="user-profile-name"
                  name="last_name"
                  value={props.form.last_name}
                  onChange={props.handleChange}
               />
            </div>

         </div>


         <div className="user-locacion-box">
            <div className="item-title-container">
               <div className="item-title-box">
                 <b className="item-title">Live</b>
               </div>

            </div>
            <div className="input-box">
               <input
                 type="text" 
                 className=""
                 name="live"
                 value={props.form.live}
                 onChange={props.handleChange}
               />
            </div>        
         </div>

         <div className="user-credential-box">
            <div className="item-title-container">
               <div className="item-title-box">
                  <b className="item-title">Credentials</b>
               </div>
            </div>
            <div className="input-box">
            <input
               type="text" 
               className=""
               name="credential"
               value={props.form.credential}
               onChange={props.handleChange}
            />
          </div>
         </div>


         <div className="user-favorite-quote-box">
            <div className="item-title-container">
               <div className="item-title-box">
                  <b className="item-title">Quote</b>
               </div>
            </div>

            <div className="input-box">
               <TextareaAutosize {...props.textAreaProps}  rows={3}/>  
            </div>   
         </div>

      </div>
   );
};


export const NavBarTitle = props  => (
  <div className="navbar-title-box">
   <b className="navbar-title">Edit Profile</b>  
  </div>    
)



export const EditContents = props  => (
  <div className="navbar-title-box">
   <b className="navbar-title">Edit Profile</b>  
   <ModalCloseBtn/>
  </div>    
)




export class DropImage extends React.Component {

   constructor(props) {
      super(props);
      this.state = {
         file: '',
         imagePreviewUrl: ''
      };

      this.handleChange = this.handleChange.bind(this);

   }



   handleChange(event) {
      event.preventDefault();
      var reader = new FileReader();
      var file = event.target.files[0];

      reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result
      });
    }

    reader.readAsDataURL(file)

    }


   getProps() {

