import React, { Component } from 'react';
import  * as action  from 'actions/actionCreators';
import { Link } from "react-router-dom";

import {NavigationBarSmallScreen,NavigationBarBigScreen } from "components/navBar";
import {store} from "index";
//import AnswersBox from "containers/answer/answer_page";
import { QuestionComponent} from "components/question_components"
import  AjaxLoader from "components/ajax-loader";
import withMain  from 'containers/higher-order-components/main';




class IndexBox extends Component {

   constructor(props) {
      super(props);
      this.state = {
        isHomeBox        : true,
        questionListById : 'filteredQuestions',
     } 

   }


  // static getDerivedStateFromError(error) {
      // Update state so the next render will show the fallback UI.
    //  return  dispatch => action.handleError(error);
  // }

   componentDidCatch(error, info) {
    // You can also log the error to an error reporting service
    console.log(error, info);
  }


   componentDidUpdate(nextProps, prevState) {
      //console.log(nextProps, this.props)


   };



   componentDidMount() {
      let questionList  = localStorage.getItem('questionList');
      questionList = JSON.parse(questionList)
      var questionListById = this.state.questionListById;
      if (questionList) {
         store.dispatch(action.getQuestionListPending(questionListById));
         store.dispatch(action.getQuestionListSuccess(questionListById, questionList));
      }else{
         this.props.getIndex();                           
      }

   }


   getProps(){
      let props = {
         isHomeBox        : this.state.isHomeBox,
         questionListById : this.state.questionListById,

      };

      return Object.assign(props, this.props );  
   };


   render() {
      let props = this.getProps()
      console.log(props)
      var index  = props.entyties.index;
      let questions      = index.questions;
      console.log(questions, index)

      return (
         <div className="home-page" id="home-page">

            <NavigationBarBigScreen {...props}/>
            <NavigationBarSmallScreen {...props}/>
            { index?
               <div>
                  { index.isLoading?
                     <div  className="page-spin-loder-box">
                        <AjaxLoader/>
                     </div>
                     : 
                     <div>
                          <Questions {...props}/> 
                     </div>
                  }

               </div>
               :
               ""
            }
         </div>
      );
   };
}





export default  withMain(IndexBox);





export const Questions = props => {
   var index  = props.entyties.index;
   let questions      = index.questions

   return (

      <div>
         { questions && questions.length?
         <div className="home-page-contents">

            { questions.map((questionObj, index) => {
               let contentsProps = {questionObj, index};

               if (questionObj.answer) {

               }else{
                  contentsProps['isHomeBox'] = props.isHomeBox;

               }

               Object.assign(contentsProps, props)  

               return (

                  <div key={index} >
                     <QuestionComponent {...contentsProps}  />
                  </div>
               )
            }
            )}
         </div>
         :
         ""

      }

      </div>
   );
};




const HomePageContents = props => {
  console.log(props)
   let state = {
      currentUser  : props.currentUser, 
      questionObj  : props.questionObj,
      };

   let questionPath = `/question/${props.questionObj.slug}/`;

   return (
      <div className="question-content">
         <div className="question-home-box">
            <div className="question-home">  
               <b className="">
                  <Link to={{pathname: questionPath, state:state }} className="question-link">
                     { props.questionObj.add_question }
                  </Link>
               </b>
            </div>


         </div>
      </div>  
   );

};
